FROM REQUEST
WHERE USERID = #{userId}

SELECT REQ_KEY, POSTCATEGORY, REQ_BOOK, REQ_DATE, USERID, REQ_CONT, REQ_PROCESS
FROM REQUEST
WHERE REQ_KEY = #{req_key}

mapper.update:
UPDATE REQUEST
SET REQ_PROCESS = #{req_process}
WHERE REQ_KEY = #{req_key}

mapper.select:
SELECT MENU_SEQ, MENU_CTGR, POSTCATEGORY, MENU_NAME
